sysauto_script.script:
// create needit records
var recordsToMake = 1000;

// declare and init the vars
var genNIRec;
var i;
var tableName = "x_58872_needit_needit";
var requestTypeArray = ["facilities", "hr", "legal"];
var selectRequestType = "";
var selectWhatNeeded = 3;
var pickDate = 1;
var requestedForArray = [{"name":"Fred Luddy","email":"fred.luddy@example.com"},{"name":"Beth Anglin","email":"beth.anglin@example.com"},{"name":"Joe Employee","email":"joe.employee@example.com"}];
var pickUser;

// for loop to create the necessary records
for (i = 0; i < recordsToMake; i++) {

	// create an empty record for the NeedIt table
	try {
		genNIRec = new GlideRecord(tableName);
		genNIRec.newRecord();

		// select a random value from the request type array
		selectRequestType = requestTypeArray[(Math.floor(Math.random() * 3))];
		genNIRec.setValue("u_request_type", selectRequestType);

		// set the what needed field value choice from the request type, either 1 or 2
		selectWhatNeeded = (Math.floor(Math.random() * 3));
		genNIRec.setValue("u_what_needed", selectWhatNeeded);

		// set the short description
		genNIRec.setValue("short_description", "Automatically created NeedIt Request: " + selectRequestType + ", " + selectWhatNeeded);

		// set the when needed field value (dat/time) to a date between yesterday and seven months ago
		var rightNow = new GlideDateTime();
		pickDate = (Math.floor(Math.random() * 210) + 1);
		rightNow.addDaysUTC(-pickDate);
		genNIRec.setValue("u_when_needed", rightNow.getDate());

		// get a random user for our record
		pickUser = (Math.floor(Math.random() * 3));
		genNIRec.u_requested_for.setDisplayValue(requestedForArray[pickUser].name);
		genNIRec.setValue("u_requested_for_email", requestedForArray[pickUser].email);

		// set the value for priority and state to Requested
		genNIRec.setValue("priority", (Math.floor(Math.random() * 5) + 1));
		genNIRec.setValue("state", 13);

		// insert the new record
		genNIRec.insert();
		gs.info("Scheduled Script Excecution Created NeedIt Request: " + selectRequestType + ", "+ selectWhatNeeded + " for " + requestedForArray[pickUser].name);
	}
	// catch exceptions
	catch (ex) {
		msg = ex.getMessage();
		gs.error("Scheduled Script Execution failed: " + msg);
	}
}
